%/% 128) |>\n    \n      # extract statistical features from each epoch\n      group_by(epoch) |>\n      summarise(\n          # Keep track of user and experiment information\n          user_id = username, \n          exp_id = expname,   \n          \n          # Activity label for the epoch = most common value (mode)\n          activity = most_common_value(c(\"-\", activity)),\n    \n          # Keep starting sample ID of the epoch\n          sampleid = sampleid[1],\n    \n          # Signal features\n          # Mean\n          mean_X1 = mean(X1), \n          mean_X2 = mean(X2), \n          mean_X3 = mean(X3),\n\n          # SD\n          sd_X1 = sd(X1), \n          sd_X2 = sd(X2),\n          sd_X3 = sd(X3),\n\n          # Median\n          median_X1 = median(X1),\n          median_X2 = median(X2),\n          median_X3 = median(X3),\n\n          # Min\n          min_X1 = min(X1),\n          min_X2 = min(X2),\n          min_X3 = min(X3),\n\n          # Max\n          max_X1 = max(X1),\n          max_X2 = max(X2),\n          max_X3 = max(X3),\n\n          # Range\n          range_X1 = range(X1)[2] - range(X1)[1],\n          range_X2 = range(X2)[2] - range(X2)[1],\n          rangeX3 = range(X3)[2] - range(X3)[1],\n\n          # IQR \n          iqr_X1 = interquartile_range(X1),\n          iqr_X2 = interquartile_range(X2),\n          iqr_X3 = interquartile_range(X3),\n\n          # Covariance\n          cov_X1X2 = cov(X1, X2),\n          cov_X1X3 = cov(X1, X3),\n          cov_X2X3 = cov(X2, X3),\n\n          # Quantiles\n          q25_X1  = quantile(X1, .25),\n          q75_X1 = quantile(X1, .75),\n          q25_X2 = quantile(X2, .25),\n          q75_X2 = quantile(X2, .75), \n          q25_X3 = quantile(X3, .25),\n          q75_X3 = quantile(X3, .75),\n\n          # Skew\n          skew_X1 = e1071::skewness(X1),\n          skew_X2 = e1071::skewness(X2),\n          skew_X3 = e1071::skewness(X3),\n\n          # Kurtosis\n          kurt_X1 = e1071::kurtosis(X1),\n          kurt_X2 = e1071::kurtosis(X2),\n          kurt_X3 = e1071::kurtosis(X3),\n\n          # Kurtosis\n          power_X1 = power(X1),\n          power_X2 = power(X2),\n          power_X3 = power(X3),\n\n          # Entropy\n          ent_X1 = entropy(X1),\n          ent_X2 = entropy(X2),\n          ent_X3 = entropy(X3),\n\n          # Signal energy \n          signal_energy_X1 = signal_energy(X1),\n          signal_energy_X2 = signal_energy(X2),\n          signal_energy_X3 = signal_energy(X3),\n\n          # Sign changes\n          sign_change_X1 = sign_changes(X1),\n          sign_change_X2 = sign_changes(X2),\n          sign_change_X3 = sign_changes(X3),\n\n            \n\n          # Lagged correlation\n          AR1_X1_lag1  = lagged_cor(X1, lag = 1),\n          AR1_X1_lag2  = lagged_cor(X1, lag = 2),\n          AR_X1X2_lag1 = lagged_cor(X1, X2, lag = 1),\n          AR_X1X2_lag2 = lagged_cor(X1, X2, lag = 2),\n          AR2_X2_lag1 = lagged_cor(X2, lag = 1),\n          AR2_X2_lag2 = lagged_cor(X2, lag = 2),\n          AR_X2X3_lag1 = lagged_cor(X2, X3, lag = 1),\n          AR_X2X3_lag2 = lagged_cor(X2, X3, lag = 2),\n          AR_X1X3_lag1 = lagged_cor(X1, X3, lag = 1),\n          AR_X1X3_lag2 = lagged_cor(X1, X3, lag = 2),\n          AR3_X3_lag1 = lagged_cor(X3, lag = 1),\n          AR3_X3_lag2 = lagged_cor(X3, lag = 2),\n        \n          n_samples = n(),\n\n      )    \n    \n    user_spect_df <- user_df |>\n    mutate(epoch = sampleid %/% 128) |>\n    reframe({\n      # ==> Start LLM\n      if (n() < 15) {\n        tibble(\n          freq = numeric(0),\n          spec1 = numeric(0),\n          spec2 = numeric(0), \n          spec3 = numeric(0)\n        )\n      } else {\n        sp <- spectrum(cbind(X1, X2, X3), span = 15, plot = FALSE)\n        tibble(\n          freq  = sp$freq,\n          spec1 = sp$spec[, 1],\n          spec2 = sp$spec[, 2],\n          spec3 = sp$spec[, 3]\n        )\n      }\n    }, .by = epoch)\n    # ==> End LLM. Kept getting this error: 'x' is shorter than kernel 'k' while using the map_dfr(), so I asked deepseek what it meant\n    \n    userfreqdom = user_spect_df %>%\n    group_by(epoch) %>%\n    summarise(\n        # Dominant frequency of signal \n        domfreq1 = freq[which.max(spec1)],\n        domfreq2 = freq[which.max(spec2)],\n        domfreq3 = freq[which.max(spec3)],\n\n        # Mean frequency of signal \n        meanfreq1 = mean_freq(freq, spec1),\n        meanfreq2 = mean_freq(freq, spec2),\n        meanfreq3 = mean_freq(freq, spec3),\n\n        # SD of signal \n        sd1 = spec_sd(spec1),\n        sd2 = spec_sd(spec2),\n        sd3 = spec_sd(spec3),\n\n        # Spectral Skew\n        spec_skew_1 = spec_skew(spec1),\n        spec_skew_2 = spec_skew(spec2),\n        spec_skew_3 = spec_skew(spec3),\n        \n        # Spectral Kurtosis \n        spec_kurt_1 = spec_kurt(spec1),\n        spec_kurt_2 = spec_kurt(spec2),\n        spec_kurt_3 = spec_kurt(spec3),\n        \n        # spectral_entropy \n        spec_ent1 = spec_entropy(spec1),\n        spec_ent2 = spec_entropy(spec2),\n        spec_ent3 = spec_entropy(spec3),\n\n        # Frequency variance\n        freq_var1 = freqvar(spec1),\n        freq_var2 = freqvar(spec2),\n        freq_var3 = freqvar(spec3),\n\n        # Spectral Bandpower High\n        spec_bands1 = spec_bands(spec1),\n        spec_bands2 = spec_bands(spec2),\n        spec_bands3 = spec_bands(spec3),\n\n        # Spectral energy\n        spec_energy1 = spec_energy(freq, spec1),\n        spec_energy2 = spec_energy(freq, spec2),\n        spec_energy3 = spec_energy(freq, spec3),\n\n        # Spectrum spread\n        spec_spread1 = spectrum_spread(freq, spec1),\n        spec_spread2 = spectrum_spread(freq, spec2),\n        spec_spread3 = spectrum_spread(freq, spec3),\n        \n    )\n\n    features = usertimedom %>%\n    left_join(userfreqdom, by = 'epoch')\n}","metadata":{"trusted":true,"execution":{"iopub.status.busy":"2025-09-24T18:02:08.058427Z","iopub.execut