user17.txt\" \"gyro_exp38_user19.txt\" \"gyro_exp39_user19.txt\"\n[25] \"gyro_exp42_user21.txt\" \"gyro_exp43_user21.txt\" \"gyro_exp44_user22.txt\" \"gyro_exp45_user22.txt\"\n[29] \"gyro_exp46_user23.txt\" \"gyro_exp47_user23.txt\" \"gyro_exp50_user25.txt\" \"gyro_exp51_user25.txt\"\n[33] \"gyro_exp52_user26.txt\" \"gyro_exp53_user26.txt\" \"gyro_exp54_user27.txt\" \"gyro_exp55_user27.txt\"\n[37] \"gyro_exp56_user28.txt\" \"gyro_exp57_user28.txt\" \"gyro_exp58_user29.txt\" \"gyro_exp59_user29.txt\"\n[41] \"gyro_exp60_user30.txt\" \"gyro_exp61_user30.txt\""},"metadata":{}}],"execution_count":10},{"cell_type":"code","source":"extractTimeDomainFeatures <- function(filename, sample_labels) {\n    \n    # extract user and experimental run ID's from file name\n    username <- str_extract(filename, \"(?<=user)\\\\d+\") |> as.integer()\n    expname  <- str_extract(filename, \"(?<=exp)\\\\d+\") |> as.integer()\n    \n    # import the sensor signals from the file\n    user01 <- read_delim(filename, delim = \" \", col_names = FALSE, progress = TRUE,\n                 col_types = \"ddd\")\n    \n    \n    # merge signals with labels \n    user_df <- \n      tibble(userid = username, trial = expname, sampleid = seq.int(0, nrow(user01) - 1)) |>\n      bind_cols(user01) |>\n      left_join(sample_labels, by = c(\"userid\", \"trial\", \"sampleid\"))\n\n      usertimedom <- user_df |>  \n      # partition into epochs and add an epoch ID variable (one epoch = 2.56 sec)\n      mutate(epoch = sampleid %/% 128) |>\n    \n      # extract statistical features from each epoch\n      group_by(epoch) |>\n      summarise(\n          # Keep track of user and experiment information\n          user_id = username, \n          exp_id = expname,   \n          \n          # Activity label for the epoch = most common value (mode)\n          activity = most_common_value(c(\"-\", activity)),\n    \n          # Keep starting sample ID of the epoch\n          sampleid = sampleid[1],\n    \n          # Signal features\n          # Mean\n          mean_X1 = mean(X1), \n          mean_X2 = mean(X2), \n          mean_X3 = mean(X3),\n\n          # SD\n          sd_X1 = sd(X1), \n          sd_X2 = sd(X2),\n          sd_X3 = sd(X3),\n\n          # Median\n          median_X1 = median(X1),\n          median_X2 = median(X2),\n          median_X3 = median(X3),\n\n          # Min\n          min_X1 = min(X1),\n          min_X2 = min(X2),\n          min_X3 = min(X3),\n\n          # Max\n          max_X1 = max(X1),\n          max_X2 = max(X2),\n          max_X3 = max(X3),\n\n          # Range\n          range_X1 = range(X1)[2] - range(X1)[1],\n          range_X2 = range(X2)[2] - range(X2)[1],\n          rangeX3 = range(X3)[2] - range(X3)[1],\n\n          # IQR \n          iqr_X1 = interquartile_range(X1),\n          iqr_X2 = interquartile_range(X2),\n          iqr_X3 = interquartile_range(X3),\n\n          # Covariance\n          cov_X1X2 = cov(X1, X2),\n          cov_X1X3 = cov(X1, X3),\n          cov_X2X3 = cov(X2, X3),\n\n          # Quantiles\n          q25_X1  = quantile(X1, .25),\n          q75_X1 = quantile(X1, .75),\n          q25_X2 = quantile(X2, .25),\n          q75_X2 = quantile(X2, .75), \n          q25_X3 = quantile(X3, .25),\n          q75_X3 = quantile(X3, .75),\n\n          # Skew\n          skew_X1 = e1071::skewness(X1),\n          skew_X2 = e1071::skewness(X2),\n          skew_X3 = e1071::skewness(X3),\n\n          # Kurtosis\n          kurt_X1 = e1071::kurtosis(X1),\n          kurt_X2 = e1071::kurtosis(X2),\n          kurt_X3 = e1071::kurtosis(X3),\n\n          # Kurtosis\n          power_X1 = power(X1),\n          power_X2 = power(X2),\n          power_X3 = power(X3),\n\n          # Entropy\n          ent_X1 = entropy(X1),\n          ent_X2 = entropy(X2),\n          ent_X3 = entropy(X3),\n\n          # Signal energy \n          signal_energy_X1 = signal_energy(X1),\n          signal_energy_X2 = signal_energy(X2),\n          signal_energy_X3 = signal_energy(X3),\n\n          # Sign changes\n          sign_change_X1 = sign_changes(X1),\n          sign_change_X2 = sign_changes(X2),\n          sign_change_X3 = sign_changes(X3),\n\n            \n\n          # Lagged correlation\n          AR1_X1_lag1  = lagged_cor(X1, lag = 1),\n          AR1_X1_lag2  = lagged_cor(X1, lag = 2),\n          AR_X1X2_lag1 = lagged_cor(X1, X2, lag = 1),\n          AR_X1X2_lag2 = lagged_cor(X1, X2, lag = 2),\n          AR2_X2_lag1 = lagged_cor(X2, lag = 1),\n          AR2_X2_lag2 = lagged_cor(X2, lag = 2),\n          AR_X2X3_lag1 = lagged_cor(X2, X3, lag = 1),\n          AR_X2X3_lag2 = lagged_cor(X2, X3, lag = 2),\n          AR_X1X3_lag1 = lagged_cor(X1, X3, lag = 1),\n          AR_X1X3_lag2 = lagged_cor(X1, X3, lag = 2),\n          AR3_X3_lag1 = lagged_cor(X3, lag = 1),\n          AR3_X3_lag2 = lagged_cor(X3, lag = 2),\n        \n          n_samples = n(),\n\n      )    \n    \n    user_spect_df <- user_df |>\n    mutate(epoch = sampleid %/% 128) |>\n    reframe({\n      # ==> Start LLM\n      if (n() < 15) {\n        tibble(\n          freq = numeric(0),\n          spec1 = numeric(0),\n          spec2 = numeric(0), \n          spec3 = numeric(0)\n        )\n      } else {\n        sp <- spectrum(cbind(X1, X2, X3), span = 15, plot = FALSE)\n        tibble(\n          freq  = sp$freq,\n          spec1 = sp$spec[, 1],\n          spec2 = sp$spec[, 2],\n          spec3 = sp$spec[, 3]\n        )\n      }\n    }, .by = epoch)\n    # ==> End LLM. Kept getting this error: 'x' is shorter than kernel 'k' while using the map_dfr(), so I asked deepseek what it meant\n    \n    userfreqdom = user_spect_df %>%\n    group_by(epoch) %>%\n    summarise(\n        # Dominant frequency of signal \n        domfreq1 = freq[which.max(spec1)],\n        domfreq2 = freq[which.max(spec2)],\n        domfreq3 = freq[which.max(spec3)],\n\n        # Mean frequency of signal \n        meanfreq1 = mean_freq(freq, spec1),\n        meanfreq2 = mean_freq(freq, spec2),\n        me